/*Table structure for table `t_cms_news` */

DROP TABLE IF EXISTS `t_cms_news`;

CREATE TABLE `t_cms_news` (
  `PK_NEWS` int(11) NOT NULL AUTO_INCREMENT,
  `C_TITLE` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `C_SHORT_CONTENT` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `C_CONTENT` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `C_IMG` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `C_DATE` date DEFAULT NULL,
  `FK_PERSON` int(11) DEFAULT NULL,
  `C_ACTIVE` int(11) DEFAULT NULL,
  `FK_TYPE` int(11) DEFAULT NULL,
  `C_VIEWS` int(11) DEFAULT NULL,
  `C_LANG` enum('en','vi') DEFAULT 'vi',
  PRIMARY KEY (`PK_NEWS`)
) ENGINE=InnoDB AUTO_INCREMENT=136 DEFAULT CHARSET=latin1;

/*Data for the table `t_cms_news` */

insert  into `t_cms_news`(`PK_NEWS`,`C_TITLE`,`C_SHORT_CONTENT`,`C_CONTENT`,`C_IMG`,`C_DATE`,`FK_PERSON`,`C_ACTIVE`,`FK_TYPE`,`C_VIEWS`,`C_LANG`) values (66,'Nokia launches $100m smart car fund','Nokia has launched a $100m (£59m) fund which will invest in firms specialising in connected and smart vehicles.','<p class=\"introduction\" id=\"story_continues_1\" style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left; font-weight: bold;\">Nokia has launched a $100m (&pound;59m) fund which will invest in firms specialising in connected and smart vehicles.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">It is seen as a key growth area and Nokia has established its presence in the sector with its mapping service Here for connected vehicles.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The move comes just days after Nokia completed the sale of its handset unit to Microsoft.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The Finnish firm has said that mapping services is one of the three key businesses that it plans to focus on.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Nokia said the fund, which will be managed by Nokia Growth Partners (NGP), will support the growth of the sector.</p>\r\n\r\n<p><span class=\"cross-head\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; display: block; font-weight: bold; margin: 0px 0px 16px; font-size: 1.231em; text-rendering: optimizelegibility;\">&#39;Surge in innovation&#39;</span></p>\r\n\r\n<div class=\"story-feature narrow\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; position: relative; margin: 0px -160px 16px 16px; width: 144px; float: right; display: inline; overflow: hidden; clear: right;\"><a class=\"hidden\" href=\"http://www.bbc.com/news/technology-27280982#story_continues_2\" style=\"color: rgb(74, 113, 148); text-decoration: none; font-weight: bold; position: absolute; top: -5000px; left: -5000px;\">Continue reading the main story</a>\r\n\r\n<h2 class=\"quote\" style=\"line-height: 16px; margin: 0px 0px 12px; padding: 6px 0px 5px; font-size: 1.231em; border-top-width: 1px; border-top-style: solid; border-top-color: rgb(216, 216, 216); border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(216, 216, 216); font-weight: bold; text-rendering: optimizelegibility; text-indent: -500px; background-image: url(http://news.bbcimg.co.uk/view/3_0_19/cream/hi/shared/img/story_sprite.png); position: relative; clear: both; background-position: 0px -188px; background-repeat: no-repeat no-repeat;\">&ldquo;<span style=\"display: block; margin: 0px 0px 5px; text-indent: -5000px;\">Start Quote</span></h2>\r\n\r\n<blockquote style=\"font-family: Arial, Helmet, Freesans, sans-serif; margin: 0px; padding: 0px; float: left; display: inline; font-weight: bold;\">\r\n<p class=\"first-child\" style=\"color: rgb(51, 51, 51); margin: 0px 0px 12px; padding: 0px; font-size: 1.231em; text-rendering: optimizelegibility; clear: left;\">Vehicles are becoming a new platform for technology adoption very similar to phones or tablet&rdquo;</p>\r\n</blockquote>\r\n<span class=\"quote-credit\" style=\"display: block; margin: 0px 0px 8px; clear: both;\">Paul Asel</span><span class=\"quote-credit-title\" style=\"display: block; margin: 0px 0px 8px; clear: both;\">Nokia Growth Partners</span></div>\r\n\r\n<p id=\"story_continues_2\" style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Firms have been looking to connect vehicles - not only to the internet, but also to each other.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The idea is that connected and &quot;smart&quot; vehicles would not only change the way drivers use their cars, but also make them more safe by sensing nearby vehicles.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">A key part of that connectivity is the ability of vehicles to access their location, and that of other objects around them.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">And as an increasing number of vehicles become inter-connected, it could provide a growth opportunity for firms specialising in the sector.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">&quot;For the last few years there has been a surge in innovation that has brought technological advances leading to safer, cleaner, increasingly connected, intelligent and more affordable vehicles,&quot; Paul Asel, partner at NGP,&nbsp;<a href=\"http://company.nokia.com/en/news/press-releases/2014/05/05/nokia-launches-100-million-connected-car-fund-managed-by-nokia-growth-partners-ngp\" style=\"color: rgb(74, 113, 148); line-height: 16px; text-decoration: none; font-weight: bold;\">said in a statement</a>.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">&quot;Vehicles are becoming a new platform for technology adoption very similar to phones or tablet.&quot;</p>\r\n\r\n<p><span class=\"cross-head\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; display: block; font-weight: bold; margin: 0px 0px 16px; font-size: 1.231em; text-rendering: optimizelegibility;\">Other players</span></p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Other firms have also been keen to tap into the sector, including Google, Apple and Intel.</p>\r\n\r\n<div class=\"caption\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; position: relative; clear: both; float: right; display: inline; margin: 0px -160px 16px 16px;\"><img alt=\"Apple CarPlay\" height=\"216\" src=\"http://news.bbcimg.co.uk/media/images/74643000/jpg/_74643845_afp_apple2.jpg\" style=\"border: 0px; -webkit-user-select: none; position: relative; letter-spacing: 0px; font-style: italic;\" width=\"384\" /><span style=\"display: block; width: 384px;\">Apple is among the tech companies eyeing the smart car market</span></div>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Earlier this year, Google announced that it is teaming up with several car manufacturers - including Audi, Honda and Hyundai - to integrate its Android operating system into their dashboards.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">That would allow the apps and music on Android smartphones and tablets to work better with onboard car systems.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Google has also added a traffic layer to Google Maps, sourced from other Android users, to inform users about traffic jams and other road issues.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Rival Apple has also unveiled its CarPlay iPhone system which allows iPhones to plug into cars.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The firm said that would allow drivers to call up maps, make calls and request music with voice commands or a touch on a vehicle&#39;s dashboard screen.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Chipmaker Intel has also previously announced a $100m fund dedicated to investing in technology aimed at accelerating the development of internet-connected cars.</p>\r\n','123.jpg','2014-05-18',10,1,1,1,'en'),(67,'Samsung ordered to pay Apple $119.6m','Samsung has been ordered to pay $119.6m (£71m) to Apple by a US court for infringing two of its patents.','<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">A jury in California delivered its verdict in a federal court in San Jose on Friday in the latest lawsuit involving the two tech giants.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Apple had sought $2bn at the trial, accusing Samsung of violating patents on smartphone features.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The court also ruled that Apple infringed Samsung&#39;s patents and awarded $158,000 in damages.</p>\r\n\r\n<div class=\"story-feature wide \" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; position: relative; margin: 0px -160px 16px 16px; width: 304px; float: right; display: inline; overflow: hidden; clear: right;\"><a class=\"hidden\" href=\"http://www.bbc.com/news/world-us-canada-27265633#story_continues_2\" style=\"color: rgb(74, 113, 148); text-decoration: none; font-weight: bold; position: absolute; top: -5000px; left: -5000px;\">Continue reading the main story</a>\r\n\r\n<h2 style=\"line-height: 16px; margin: 0px 0px 8px; padding: 11px 0px 12px; font-size: 1.231em; border-top-width: 1px; border-top-style: solid; border-top-color: rgb(216, 216, 216); border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(216, 216, 216); font-weight: bold; text-rendering: optimizelegibility;\">Analysis</h2>\r\n\r\n<div class=\"byline\" style=\"position: relative; padding: 1px 0px 16px; border: none; margin: 0px; clear: both; overflow: hidden;\"><span class=\"byline-name\" style=\"display: block; margin: 0px; font-size: 1.231em; font-weight: bold; text-rendering: optimizelegibility; padding-bottom: 2px; border: none;\">Richard Taylor</span><span class=\"byline-title\" style=\"display: block; margin: 0px;\">North America technology correspondent</span></div>\r\n\r\n<p style=\"margin: 0px 0px 12px; padding: 0px; text-rendering: auto; clear: left;\">The verdict will probably come as a blow for Apple, which portrayed Samsung as resorting to &quot;the dark side of intentional copying&quot;.</p>\r\n\r\n<p style=\"margin: 0px 0px 12px; padding: 0px; text-rendering: auto; clear: left;\">The figure would appear to reflect the jury&#39;s belief that Apple&#39;s settlement claim was unfairly inflated; Samsung argued all along that it should be far lower than the $2.2bn sought, not least because some of the patents were never even incorporated into the iPhone&#39;s software.</p>\r\n\r\n<p style=\"margin: 0px 0px 12px; padding: 0px; text-rendering: auto; clear: left;\">They say lawyers are the only ones who win from patent wars. In this case that could well prove true - with all the mudslinging and embarrassing leaks of internal memos witnessed in the courtroom over the past month, neither company&#39;s reputation has been enhanced.</p>\r\n</div>\r\n\r\n<p id=\"story_continues_2\" style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Apple had sought $2.2bn after accusing Samsung of infringing five of its patents covering functions such as the &quot;slide to unlock&quot; from its devices.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Samsung denied any wrongdoing and sought $6m after arguing Apple had infringed two of its smartphone patents related to camera use and video transmission.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">&quot;Though this verdict is large by normal standards, it is hard to view this outcome as much of a victory for Apple,&#39;&#39; said Brian Love, a Santa Clara University law professor.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">&quot;This amount is less than 10% of the amount Apple requested and probably doesn&#39;t surpass by too much the amount Apple spent litigating this case.&#39;&#39;</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">This verdict marks the latest legal battle over intellectual property between the world&#39;s top two smartphone makers.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Apple and Samsung have been fighting patent battles for years and across many countries.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Two years ago, a separate jury ordered Samsung to pay Apple $930m after finding it had used Apple technology.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">That verdict is still being challenged by Samsung.</p>\r\n','a.jpg','2014-05-18',10,1,1,1,'en'),(68,'Outsider Juan Carlos Varela wins Panama election','Opposition leader Juan Carlos Varela has won the presidential election in Panama with almost 40% of the votes.','<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Mr Varela, who is currently the vice-president, had distanced himself from outgoing President Ricardo Martinelli.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Correspondents say Mr Varela has taken credit for Mr Martinelli&#39;s economic success, but has promised a cleaner, more transparent government.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The president&#39;s preferred candidate, the governing party contender Jose Domingo Arias, came second.</p>\r\n\r\n<p><span class=\"cross-head\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; display: block; font-weight: bold; margin: 0px 0px 16px; font-size: 1.231em; text-rendering: optimizelegibility;\">&#39;May God help us&#39;</span></p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">President Martinelli had actively supported the campaign of Mr Arias, 50, and the leader&#39;s wife Marta Linares was the candidate&#39;s running mate.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Critics said his support for the Arias-Linares team was an attempt by Mr Martinelli to hold on to the reins of power.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Under the Panamanian constitution, presidents are obliged to step down after one term and are banned from running for the two following terms.</p>\r\n\r\n<div class=\"caption full-width\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; position: relative; clear: both; float: none; margin: 0px 0px 15px;\"><img alt=\"Panama\'s outgoing President Ricardo Martinelli  visits the Electoral Tribunal building after the official election results were released in Panama City on 4 May, 2014\" height=\"351\" src=\"http://news.bbcimg.co.uk/media/images/74643000/jpg/_74643646_dd38b255-fb58-42cb-bf76-f1c6be4b3359.jpg\" style=\"border: 0px; -webkit-user-select: none; position: relative; letter-spacing: 0px; font-style: italic;\" width=\"624\" /><span style=\"display: block; width: 624px;\">Outgoing President Ricardo Martinelli did not hide his disappointment at his preferred candidate&#39;s loss</span></div>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Mr Varela, a former centre-right ally of Mr Martinelli, fell out with the president after he was dismissed from his post as foreign minister in 2011.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">After he had achieved an unassailable lead in the poll, Mr Varela, 50, told Reuters news agency that &quot;better times are on their way&quot;.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Alluding to allegations of corruption against Mr Martinelli&#39;s government, he said his would be &quot;an honest, humane government of national unity&quot;.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Hearing of Mr Varela&#39;s win, Mr Martinelli said &quot;I know the candidate, and really, may God help us!&quot;.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">He said in light of Mr Varela&#39;s success he would change his plans of &quot;enjoying life&quot; and go into opposition instead.</p>\r\n\r\n<p><span class=\"cross-head\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; display: block; font-weight: bold; margin: 0px 0px 16px; font-size: 1.231em; text-rendering: optimizelegibility;\">&#39;One flag&#39;</span></p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Mr Varela struck a conciliatory note, saying it was time &quot;to put the party banners away and to govern under one flag, that of Panama&quot;.</p>\r\n\r\n<div class=\"caption full-width\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; position: relative; clear: both; float: none; margin: 0px 0px 15px;\"><img alt=\"A supporter of Panamanian presidential candidate for the Democratic Change party, Jose Domingo Arias on 4 May, 2014\" height=\"351\" src=\"http://news.bbcimg.co.uk/media/images/74643000/jpg/_74643750_e0d556ed-90b4-4d48-a9a0-e89c3d86e453.jpg\" style=\"border: 0px; -webkit-user-select: none; position: relative; letter-spacing: 0px; font-style: italic;\" width=\"624\" /><span style=\"display: block; width: 624px;\">Jose Domingo Arias had led in the pre-election polls and his defeat surprised many of his supporters</span></div>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Despite his unexpected win in the presidential poll, Mr Varela&#39;s party only got 11 of the 71 seats in Congress which were also up for election.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">After his swearing-in on 1 July, Mr Varela will therefore have to form alliances with the opposition to push his planned reforms through the legislature.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">He faces the challenge of maintaining buoyant growth while dealing with economic inequality, with a quarter of the population living in poverty.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Mr Varela will also have oversight of a major expansion of the Panama Canal, which accounts for 8% of gross domestic product in the country.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Recent discontent led to a nationwide construction strike over pay, which halted work on the canal and thousands of other projects.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Among the first leaders to congratulate Mr Varela was Venezuelan President Nicolas Maduro.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Venezuela earlier this year broke ties with Panama accusing the Central American nation of fomenting a coup against President Maduro by offering support to a Venezuelan opposition leader.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Mr Maduro said he hoped &quot;to move towards an improvement of our relations&quot;.</p>\r\n','pl3.jpg','2014-05-18',10,1,1,NULL,'en'),(69,'Five Honduran youth gang members killed in jail fight','Raids in Honduran jails routinely uncover hundreds of weapons used in deadly prison fights\r\nContinue reading the main story\r\nRelated Stories\r\n\r\nHonduras troops take over at prison\r\nInmates \'running Honduras prisons\'\r\nPrisoners surrender in Honduras\r\nFive youths have been killed in a gang fight in a prison in Honduras.','<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Officials said the five, all of them under 18, belonged to the M18 street gang and were attacked by members of the rival Mara Salvatrucha gang.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The fight happened in El Carmen jail in San Pedro Sula.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Last year, the Inter-American Commission on Human Rights accused Honduras of giving up on rehabilitating criminals and leaving prisons to be controlled by their inmates.</p>\r\n\r\n<p><span class=\"cross-head\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; display: block; font-weight: bold; margin: 0px 0px 16px; font-size: 1.231em; text-rendering: optimizelegibility;\">Overcrowded and underfunded</span></p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Police said armed members of the Mara Salvatrucha gang threatened the guards and forced their way at gunpoint into the area where M18 gang members are held.</p>\r\n\r\n<div class=\"caption full-width\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; position: relative; clear: both; float: none; margin: 0px 0px 15px;\"><br />\r\n<span style=\"display: block; width: 624px;\">Many Honduran prisons are controlled by gangs, which mark their territory with graffiti</span></div>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">They then opened fire, killing five and injuring one. Neighbours reported hearing an explosion, but police denied grenades had been used in the gang fight.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">There are conflicting reports on whether the assailants were also inmates or whether they had forced their way in from the outside.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Officials said three of the alleged assailants had been arrested.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Honduran prisons are notoriously overcrowded, understaffed and wracked by violence.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">In February 2012, some 360 prisoners died in a fire in Comayagua prison north of Tegucigalpa.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Non-governmental organisations have urged the government to improve its prison system but little has been done since the deadly fire.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">San Pedro Sula has the world&#39;s highest homicide rate for any city not in a warzone, according to United Nations figures.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The government says it is overwhelmed by the sheer number of people awaiting trial, or already sentenced.</p>\r\n','doanhnghiep.jpg','2014-05-18',10,1,2,1,'en'),(70,'Libya Congress confirms new PM Ahmed Maitig','Ahmed Maitig is Libya\'s fifth prime minister to be appointed in the past two and a half years\r\nContinue reading the main story\r\nPost-Gaddafi\r\n\r\nSecrets of the Sahara\r\n\'I witnessed Benghazi uprising\'\r\nGuide to the militias\r\nLand of the gun\r\nLibya\'s interim parliament has confirmed the appointment of Ahmed Maitig as the new prime minister.','<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The General National Congress elected the businessman in a televised vote on Sunday, and he was sworn in.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">But acting Congress chairman Ezzedine Al-Amawi later declared the ballot illegal after a row over the procedure.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">On Monday, a decree signed by the head of Congress, Nuri Abu Sahmein - who was absent on Sunday - named Mr Maitig as interim prime minister.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Mr Maitig, 42, is the country&#39;s fifth PM to be appointed in the past two and a half years.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">His predecessor, Abdullah al-Thinni, quit last month following a gun attack on his family.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Libya has been plagued by instability since armed groups toppled Muammar Gaddafi&#39;s regime in 2011.</p>\r\n\r\n<p><span class=\"cross-head\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; display: block; font-weight: bold; margin: 0px 0px 16px; font-size: 1.231em; text-rendering: optimizelegibility;\">Controversy</span></p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Mr Maitig, from Misrata, will have two weeks to propose a cabinet, which will need to be approved by the Congress.</p>\r\n\r\n<div class=\"caption full-width\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; position: relative; clear: both; float: none; margin: 0px 0px 15px;\"><img alt=\"Members of the special body tasked to draft a new constitution for Libya and members of the General National Congress gather during the body\'s first meeting in Bayda on 21 April 2014. \" height=\"351\" src=\"http://news.bbcimg.co.uk/media/images/74637000/jpg/_74637351_reutres.jpg\" style=\"border: 0px; -webkit-user-select: none; position: relative; letter-spacing: 0px; font-style: italic;\" width=\"624\" /><span style=\"display: block; width: 624px;\">Elections for a new parliament are expected later this year to replace the General National Congress</span></div>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">This may prove to be a difficult task following the controversy over his appointment, says the BBC&#39;s Rana Jawad in Tripoli.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Some deputies had claimed the vote on Sunday was illegal, arguing that it continued after Mr Amawi had declared the session over. Other members argued that no laws were violated.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Mr Amawi said it was unconstitutional and ordered Libya&#39;s caretaker PM, Mr Thinni, to remain in power.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">But the new appointment was ratified on Monday in a decree signed by Mr Sahmein on his return to parliament.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Mr Maitig had clinched 121 votes in the 185-seat chamber - just exceeding the 120 required, the ruling said.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The latest quarrel in Congress is rooted in political and ideological splits between powerful party blocs within the legislative body, our correspondent says.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The vote was originally scheduled to take place last Tuesday, but it was interrupted when gunmen stormed the parliament, forcing the deputies to evacuate. The building has been stormed several times by gunmen over the last year and a half.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Elections for a new parliament that will replace the General National Congress are expected later this year, and a new cabinet is expected to be formed.</p>\r\n','tq.jpg','2014-05-18',10,1,2,NULL,'en'),(71,'Oscar Pistorius \'broken\' after shooting Reeva Steenkamp','South African athlete Oscar Pistorius \"was broken\" after shooting his girlfriend, his neighbour has said, as the murder trial resumed after a two-week break.','<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Johan Stander was the first person the athlete called after shooting Reeva Steenkamp on Valentine&#39;s Day last year.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The South African Olympic sprinter denies intentionally killing her.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">He says he shot through a toilet door while in a state of panic, fearing there was an intruder in his house.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The trial resumed after an Easter break.</p>\r\n\r\n<div class=\"story-feature wide \" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; position: relative; margin: 0px -160px 16px 16px; width: 304px; float: right; display: inline; overflow: hidden; clear: right;\"><a class=\"hidden\" href=\"http://www.bbc.com/news/world-africa-27280194#story_continues_2\" style=\"color: rgb(74, 113, 148); text-decoration: none; font-weight: bold; position: absolute; top: -5000px; left: -5000px;\">Continue reading the main story</a>\r\n\r\n<h2 style=\"line-height: 16px; margin: 0px 0px 8px; padding: 11px 0px 12px; font-size: 1.231em; border-top-width: 1px; border-top-style: solid; border-top-color: rgb(216, 216, 216); border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(216, 216, 216); font-weight: bold; text-rendering: optimizelegibility;\">&nbsp;</h2>\r\n</div>\r\n\r\n<p id=\"story_continues_2\" style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Mr Stander implied that he thought Mr Pistorius&#39; reactions showed that the shooting had been a mistake.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">&quot;I saw the truth that morning and I feel it,&quot; he told the court.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">&quot;He was desperate to save her [and] prayed to God.&quot;</p>\r\n\r\n<p><span class=\"cross-head\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; display: block; font-weight: bold; margin: 0px 0px 16px; font-size: 1.231em; text-rendering: optimizelegibility;\">Recent break-ins</span></p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The BBC&#39;s Pumza Fihlani who was at court in the capital, Pretoria, says the athlete wore a black suit, black tie and crisp white shirt, black glasses and his hair had been cut.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The Paralympic athlete, 27, had a pen in his hand and listened intently to his neighbour, she says.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Mr Stander, who lives about 350m (380 yards) from Mr Pistorius said he received a call at 03.18 on 14 February 2013.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">He said the athlete told him: &quot;I shot Reeva. I thought she was an intruder, please come quick.&quot;</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Mr Stander said that when he and his family arrived at the house, they saw Mr Pistorius coming downstairs with Ms Steenkamp in his arms.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">&quot;I could see she had a head-wound,&quot; Mr Stander said.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">&quot;He was broken. He was screaming, he was crying, he was praying,&quot; he said, his voice breaking with emotion.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">&quot;It&#39;s not something I would like to experience again,&quot; he said.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">&quot;He asked us to assist him to take her to hospital.&quot;</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Mr Stander also said there had been several recent break-ins in the private estate where he and Mr Pistorius live.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">However, under cross-examination, he accepted that it was a safe place to live.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Carice Viljoen, Mr Stander&#39;s daughter who arrived with him at Mr Pistorius&#39; home, broke down in court as she described seeing the accused carry Ms Steenkamp down the staircase.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The athlete was begging Ms Steenkamp to stay alive, she told the court. &quot;Stay with me, my love,&quot; she quoted him as saying.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Ms Viljoen followed the athlete inside his home when he went to get identification documents because she thought he was going to shoot himself, she added.</p>\r\n\r\n<div class=\"caption full-width\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; position: relative; clear: both; float: none; margin: 0px 0px 15px;\"><img alt=\"Oscar Pistorius and Reeva Steenkamp at an awards ceremony in Johannesburg - 14 February 2013\" height=\"351\" src=\"http://news.bbcimg.co.uk/media/images/74641000/jpg/_74641420_74641419.jpg\" style=\"border: 0px; -webkit-user-select: none; position: relative; letter-spacing: 0px; font-style: italic;\" width=\"624\" /><span style=\"display: block; width: 624px;\">The couple had been dating for just a few months when the incident happened in 2013</span></div>\r\n\r\n<div class=\"caption full-width\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; position: relative; clear: both; float: none; margin: 0px 0px 15px;\"><img alt=\"State prosecutor Gerrie Nel questions Oscar Pistorius - 15 April 2014\" height=\"351\" src=\"http://news.bbcimg.co.uk/media/images/74640000/jpg/_74640934_74640933.jpg\" style=\"border: 0px; -webkit-user-select: none; position: relative; letter-spacing: 0px; font-style: italic;\" width=\"624\" /><span style=\"display: block; width: 624px;\">Prosecutor Gerrie Nel accused Mr Pistorius of &quot;tailoring his evidence&quot; during his cross-examination</span></div>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Before the Easter break, the athlete faced several days of cross-examination from the state prosecutor Gerrie Nel, who accused him of using emotional outbursts &quot;as an escape&quot;.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">As well as neighbours, the athlete&#39;s defence team is expected to call on a ballistics expert.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">A psychologist is also set to be called to speak about Mr Pistorius&#39; disability and his acute sense of vulnerability.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The prosecution has sought to show a pattern of reckless behaviour by the athlete and has argued that a reasonable man would have checked before firing four bullets through a locked door.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">If found guilty, the 27-year-old - a national sporting hero and double amputee dubbed the &quot;blade runner&quot; because of the prosthetic limbs he wears to race - could face life imprisonment.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Ms Steenkamp, 29, was a model, celebrity TV star and law graduate.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">If Mr Pistorius is acquitted of murder, the court must consider an alternative charge of culpable homicide, for which he could receive about 15 years in prison.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">He also faces charges of illegally firing a gun in public and of illegally possessing ammunition, both of which he denies.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">There are no juries at trials in South Africa, and his fate will ultimately be decided by the judge, assisted by two assessors.</p>\r\n\r\n<div class=\"ns-panel ns-wide-include ns-panel--interactive\" id=\"pistoriushousetrialnew-4331970\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; position: relative;\">\r\n<div class=\"ns-panel__clickable\" style=\"color: rgb(255, 255, 255); background-color: rgb(0, 0, 0); float: left; font-weight: 600; margin-bottom: 0.5em; padding: 4px; text-transform: uppercase;\">INTERACTIVE</div>\r\n\r\n<div class=\"ns-panel__hotspots ns-panel__hotspots--static-t\" style=\"clear: both; margin-bottom: 1em; position: relative; height: 351px;\"><img alt=\"3D impression of Pistorius house\" class=\"no-replace\" height=\"351\" src=\"http://news.bbcimg.co.uk/news/special/panels/14/mar/pistoriushousetrialnew/img/graphic_1393589988.gif\" style=\"border: 0px; -webkit-user-select: none; letter-spacing: 0px; font-style: italic;\" width=\"624\" /></div>\r\n</div>\r\n','file.396407.jpg','2014-05-18',10,1,3,NULL,'en'),(72,'Brazil football fan killed in Recife at stadium','A Brazilian football fan has died after being hit by a toilet bowl thrown at the end of a Second Division match in the north-eastern city of Recife.','<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Paulo Ricardo Gomes da Silva was struck as clashes took place near one of the entrances to Arruda stadium.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Another three people were reportedly hurt as toilet bowls were ripped out of stadium facilities and hurled at rival fans below.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Recife is one of the host cities of the World Cup, due to start on 12 June.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\"><br />\r\n<span style=\"font-size: 1.077em;\">Brazil&#39;s football association decided on Saturday to ban matches in the Arruda stadium until a court of sport has ruled on the case.</span>But the matches will be hosted at another stadium in Recife, the Arena Pernambuco, which has been especially built for the tournament.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The clashes took place on Friday night after local side Santa Cruz drew 1-1 with Parana, from southern Brazil.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">The visiting side&#39;s fans were leaving the stadium when the toilet bowls were thrown out of the upper tiers of the Arruda stadium.</p>\r\n\r\n<div class=\"caption full-width\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; position: relative; clear: both; float: none; margin: 0px 0px 15px;\"><img alt=\"Map of Brazil\" height=\"171\" src=\"http://news.bbcimg.co.uk/media/images/74625000/gif/_74625626_brazil_recife_sept2012.gif\" style=\"opacity: 0.9; letter-spacing: 0px; border: 0px; -webkit-user-select: none; position: relative; font-style: italic;\" width=\"304\" /><img alt=\"\" src=\"/mvc4/public/img.newsimages/1525009_669438133103999_20547437_n.jpg\" style=\"width: 851px; height: 315px;\" /></div>\r\n\r\n<div class=\"caption full-width\" style=\"color: rgb(80, 80, 80); font-family: Arial, Helmet, Freesans, sans-serif; line-height: 16px; position: relative; clear: both; float: none; margin: 0px 0px 15px;\"><img alt=\" Arena Pernambuco in Recife (file pic)\" height=\"351\" src=\"http://news.bbcimg.co.uk/media/images/74622000/jpg/_74622130_021818852-1.jpg\" style=\"border: 0px; -webkit-user-select: none; position: relative; letter-spacing: 0px; font-style: italic;\" width=\"624\" /><span style=\"display: block; width: 624px;\">World Cup matches will be played at a specially built stadium in Recife, Arena Pernambuco</span></div>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">Police had told the visiting fans to leave the stadium earlier to avoid clashes, local reports said. But they were ambushed by a faction of the Santa Cruz fans.</p>\r\n\r\n<p style=\"font-family: Arial, Helmet, Freesans, sans-serif; line-height: 18px; margin: 0px 0px 18px; padding: 0px; font-size: 1.077em; text-rendering: auto; clear: left;\">An investigation has begun into the incident. Earlier this year, Santa Cruz supporters hurled toilet bowls at fans of their local rivals, Sport.</p>\r\n','tq.jpg','2014-05-15',10,1,3,2,'en'),(73,'Việt Nam sẽ áp dụng mọi biện pháp để bảo vệ lợi ích quốc gia','Việt Nam sẽ áp dụng mọi biện pháp để bảo vệ lợi ích quốc gia\r\nPhó thủ tướng, Bộ trưởng Ngoại giao Phạm Bình Minh hôm nay điện đàm với Ủy viên Quốc vụ Trung Quốc Dương Khiết Trì, phản đối việc giàn khoan HD-981 và nhiều tàu Trung Quốc hoạt động tại khu vực thuộc thềm lục địa Việt Nam.','<p>&nbsp;</p>\r\n\r\n<div id=\"left_calculator\" style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: normal;\">\r\n<div class=\"fck_detail width_common\" style=\"margin: 0px; padding: 0px 0px 10px; width: 480.015625px; float: left; overflow: hidden; font-size: 14px; color: rgb(51, 51, 51);\">&nbsp;\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px;\"><span style=\"margin: 0px; padding: 0px;\">Trong cuộc điện đ&agrave;m, Ph&oacute; Thủ tướng, Bộ trưởng Phạm B&igrave;nh Minh nhấn mạnh việc Trung Quốc đơn phương đưa gi&agrave;n khoan HD-981 v&agrave; một lượng&nbsp;lớn t&agrave;u c&aacute;c loại, kể cả t&agrave;u qu&acirc;n sự v&agrave;o hoạt động&nbsp;</span>tại khu vực thuộc l&ocirc; dầu kh&iacute; 143 thuộc thềm lục địa Việt Nam từ ng&agrave;y 1/5 đến nay<span style=\"margin: 0px; padding: 0px;\">&nbsp;l&agrave; bất hợp ph&aacute;p.</span></p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px;\"><span style=\"margin: 0px; padding: 0px;\">Việc l&agrave;m n&agrave;y đi ngược lại luật ph&aacute;p v&agrave; th&ocirc;ng lệ quốc tế, vi phạm nghi&ecirc;m trọng chủ quyền của Việt Nam đối với Ho&agrave;ng Sa, quyền chủ quyền v&agrave; quyền t&agrave;i ph&aacute;n đối với c&aacute;c v&ugrave;ng đặc quyền kinh tế v&agrave; thềm lục địa của Việt Nam. Việc n&agrave;y cũng ảnh hưởng ti&ecirc;u cực đến sự tin cậy ch&iacute;nh trị v&agrave; c&aacute;c mặt hợp t&aacute;c giữa hai nước, tổn thương t&igrave;nh cảm của người d&acirc;n Việt Nam.</span></p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px;\">Việt Nam kh&ocirc;ng thể chấp nhận v&agrave; ki&ecirc;n quyết phản đối việc l&agrave;m n&agrave;y của Trung Quốc; y&ecirc;u cầu Trung Quốc r&uacute;t hết gi&agrave;n khoan HD-981 v&agrave; c&aacute;c t&agrave;u hộ tống ra khỏi khu vực v&agrave; c&ugrave;ng đ&agrave;m ph&aacute;n để xử l&yacute; những bất đồng xung quanh vấn đề n&agrave;y, th&ocirc;ng c&aacute;o của Bộ Ngoại giao dẫn lời Ph&oacute; thủ tướng n&oacute;i.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px;\">Ph&oacute; Thủ tướng, Bộ trưởng Phạm B&igrave;nh Minh khẳng định, Việt Nam c&oacute; đầy đủ căn cứ ph&aacute;p l&yacute; v&agrave; lịch sử khẳng định chủ quyền đối với Ho&agrave;ng Sa, Trường Sa v&agrave; quyền chủ quyền, quyền t&agrave;i ph&aacute;n đối với v&ugrave;ng đặc quyền kinh tế, thềm lục địa theo c&aacute;c quy định của C&ocirc;ng ước Li&ecirc;n hợp quốc về Luật biển 1982.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px;\">Việt Nam sẽ &aacute;p dụng mọi biện ph&aacute;p ph&ugrave; hợp cần thiết để bảo vệ c&aacute;c quyền v&agrave; lợi &iacute;ch ch&iacute;nh đ&aacute;ng của m&igrave;nh. Đồng thời, Việt Nam lu&ocirc;n thể hiện thiện ch&iacute;, ki&ecirc;n tr&igrave; giải quyết thỏa đ&aacute;ng bất đồng th&ocirc;ng qua đ&agrave;m ph&aacute;n, đối thoại v&agrave; c&aacute;c biện ph&aacute;p h&ograve;a b&igrave;nh kh&aacute;c theo đ&uacute;ng nhận thức chung của L&atilde;nh đạo cấp cao hai nước, Thỏa thuận về c&aacute;c Nguy&ecirc;n tắc cơ bản chỉ đạo giải quyết vấn đề tr&ecirc;n biển, ph&ugrave; hợp với c&aacute;c quy định v&agrave; thực tiễn Luật ph&aacute;p quốc tế, nhất l&agrave; C&ocirc;ng ước Li&ecirc;n hợp quốc về Luật biển 1982, kh&ocirc;ng để vấn đề n&agrave;y tổn hại đến sự tin cậy ch&iacute;nh trị v&agrave; hợp t&aacute;c giữa hai b&ecirc;n.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px;\">Trước đ&oacute;, chiều 4/5, Thứ trưởng Ngoại giao, Trưởng đo&agrave;n đ&agrave;m ph&aacute;n Ch&iacute;nh phủ về bi&ecirc;n giới l&atilde;nh thổ ph&iacute;a Việt Nam Hồ Xu&acirc;n Sơn đ&atilde; điện đ&agrave;m với Thứ trưởng Ngoại giao, Trưởng đo&agrave;n đ&agrave;m ph&aacute;n Ch&iacute;nh phủ về bi&ecirc;n giới l&atilde;nh thổ ph&iacute;a Trung Quốc Lưu Chấn D&acirc;n giao thiệp nghi&ecirc;m t&uacute;c vụ việc tr&ecirc;n.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px;\">C&ugrave;ng ng&agrave;y, Đại diện Bộ Ngoại giao Việt Nam đ&atilde; gặp Đại biện Đại sứ qu&aacute;n Trung Quốc tại H&agrave; Nội trao C&ocirc;ng h&agrave;m của Bộ Ngoại giao Việt Nam gửi Bộ Ngoại giao Trung Quốc.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px;\">C&ocirc;ng h&agrave;m nhấn mạnh hoạt động của gi&agrave;n khoan HD-981 v&agrave; t&agrave;u Trung Quốc đ&atilde; &quot;x&acirc;m phạm nghi&ecirc;m trọng chủ quyền, quyền chủ quyền v&agrave; quyền t&agrave;i ph&aacute;n của Việt Nam đối với quần đảo Ho&agrave;ng Sa, v&ugrave;ng đặc quyền kinh tế v&agrave; thềm lục địa của Việt Nam, cũng như đ&atilde; vi phạm nghi&ecirc;m trọng c&aacute;c thỏa thuận giữa L&atilde;nh đạo hai nước, tinh thần v&agrave; lời văn của Tuy&ecirc;n bố DOC, c&aacute;c quy định c&oacute; li&ecirc;n quan của luật ph&aacute;p quốc tế&quot;,&nbsp;y&ecirc;u cầu Trung Quốc &quot;r&uacute;t ngay gi&agrave;n khoan n&oacute;i tr&ecirc;n c&ugrave;ng c&aacute;c t&agrave;u, thiết bị, nh&acirc;n sự c&oacute; li&ecirc;n quan ra khỏi khu vực l&ocirc; 143 thuộc thềm lục địa Việt Nam v&agrave; kh&ocirc;ng để t&aacute;i diễn c&aacute;c h&agrave;nh động tương tự.&quot;</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px;\">C&ocirc;ng h&agrave;m khẳng định &quot;Việt Nam hết sức coi trọng quan hệ hợp t&aacute;c hữu nghị v&agrave; đối t&aacute;c hợp t&aacute;c chiến lược to&agrave;n diện với Trung Quốc, sẵn s&agrave;ng th&ocirc;ng qua c&aacute;c cơ chế đ&agrave;m ph&aacute;n song phương để giải quyết h&ograve;a b&igrave;nh mọi tranh chấp, bất đồng tr&ecirc;n biển giữa hai nước tr&ecirc;n cơ sở luật ph&aacute;p quốc tế, C&ocirc;ng ước Li&ecirc;n hợp quốc về Luật Biển năm 1982 v&agrave; Thỏa thuận về những nguy&ecirc;n tắc cơ bản chỉ đạo giải quyết vấn đề tr&ecirc;n biển giữa Việt Nam v&agrave; Trung Quốc.&quot;</p>\r\n</div>\r\n</div>\r\n','anh-minh-8092-1399435740.jpg','2014-05-18',10,1,1,21,'vi'),(74,'Điện Biên Phủ 20 năm trước trong mắt phóng viên Mỹ','Chuyến trở về Điện Biên Phủ của Đại tướng Võ Nguyên Giáp năm 1994 hiện ra chân thực và sống động, dưới ống kính của nhiếp ảnh gia Catherine Karnow.','<div class=\"item_slide_show\" style=\"margin: 0px auto 10px; padding: 0px; clear: both; color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: normal;\">\r\n<div class=\"desc_cation\" style=\"margin: 0px; padding: 10px 13.1875px; width: 633.609375px; float: left; background-color: rgb(245, 245, 245);\">\r\n<p style=\"margin: 0px; padding: 0px;\">Đầu th&aacute;ng 5/1994, Catherine Karnow trở th&agrave;nh nhiếp ảnh gia phương T&acirc;y duy nhất được th&aacute;p t&ugrave;ng Đại tướng V&otilde; Nguy&ecirc;n Gi&aacute;p trong chuyến thăm lại Điện Bi&ecirc;n Phủ, nh&acirc;n kỷ niệm 40 năm chiến thắng lịch sử. Tại Mường Phăng, trong l&uacute;c chờ đợi trực thăng của Đại tướng hạ c&aacute;nh, b&agrave; tranh thủ chụp ảnh người d&acirc;n địa phương.&nbsp;</p>\r\n</div>\r\n\r\n<div class=\"clear\" style=\"margin: 0px; padding: 0px; clear: both; font-size: 0px; line-height: 0;\">&nbsp;</div>\r\n</div>\r\n\r\n<div class=\"item_slide_show\" style=\"margin: 0px auto 10px; padding: 0px; clear: both; color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: normal;\">\r\n<div class=\"block_thumb_slide_show\" style=\"margin: 0px; padding: 0px; width: 660.015625px; float: left; position: relative; text-align: center; font-size: 0px; line-height: 0;\"><img alt=\"\" class=\"left\" data-component-caption=\"&lt;p&gt;\r\n	Trẻ em ở Mường Phăng chờ đợi rất lâu dưới nắng nóng để được thấy Tướng Giáp.&nbsp;&lt;/p&gt;\r\n\" data-reference-id=\"19086647\" src=\"http://l.f30.img.vnecdn.net/2014/05/07/General-Giap-24-Proc-1399431707_660x0.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; line-height: 0; max-width: 100%; float: none; cursor: all-scroll;\" /><a class=\"btn_icon_show_slide_show\" style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); outline: none; height: 28px; width: 28px; display: block; background-image: url(http://st.f3.vnecdn.net/responsive/c/v13/images/icons/icon_zoom.png); position: absolute; right: 0px; top: 0px; background-position: 0% 0%; background-repeat: no-repeat no-repeat;\">&nbsp;</a></div>\r\n\r\n<div class=\"desc_cation\" style=\"margin: 0px; padding: 10px 13.1875px; width: 633.609375px; float: left; background-color: rgb(245, 245, 245);\">\r\n<p style=\"margin: 0px; padding: 0px;\">Trẻ em ở Mường Phăng chờ đợi rất l&acirc;u dưới nắng n&oacute;ng để được thấy Tướng Gi&aacute;p.&nbsp;</p>\r\n</div>\r\n\r\n<div class=\"clear\" style=\"margin: 0px; padding: 0px; clear: both; font-size: 0px; line-height: 0;\">&nbsp;</div>\r\n</div>\r\n\r\n<div class=\"item_slide_show\" style=\"margin: 0px auto 10px; padding: 0px; clear: both; color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: normal;\">\r\n<div class=\"block_thumb_slide_show\" style=\"margin: 0px; padding: 0px; width: 660.015625px; float: left; position: relative; text-align: center; font-size: 0px; line-height: 0;\"><img alt=\"\" class=\"left\" data-component-caption=\"&lt;p&gt;\r\n	Catherine Karnow ấn tượng trước những phụ nữ dân tộc nơi đây, nhưng tiếc vì không có đủ phim để chụp.&nbsp;&lt;/p&gt;\r\n\" data-reference-id=\"19086648\" src=\"http://l.f32.img.vnecdn.net/2014/05/07/General-Giap-22-Proc-1399431706_660x0.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; line-height: 0; max-width: 100%; float: none; cursor: all-scroll;\" /><a class=\"btn_icon_show_slide_show\" style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); outline: none; height: 28px; width: 28px; display: block; background-image: url(http://st.f3.vnecdn.net/responsive/c/v13/images/icons/icon_zoom.png); position: absolute; right: 0px; top: 0px; background-position: 0% 0%; background-repeat: no-repeat no-repeat;\">&nbsp;</a></div>\r\n\r\n<div class=\"desc_cation\" style=\"margin: 0px; padding: 10px 13.1875px; width: 633.609375px; float: left; background-color: rgb(245, 245, 245);\">\r\n<p style=\"margin: 0px; padding: 0px;\">Catherine Karnow ấn tượng trước những phụ nữ d&acirc;n tộc nơi đ&acirc;y, nhưng tiếc v&igrave; kh&ocirc;ng c&oacute; đủ phim để chụp.&nbsp;</p>\r\n</div>\r\n\r\n<div class=\"clear\" style=\"margin: 0px; padding: 0px; clear: both; font-size: 0px; line-height: 0;\">&nbsp;</div>\r\n</div>\r\n\r\n<div class=\"item_slide_show\" style=\"margin: 0px auto 10px; padding: 0px; clear: both; color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: normal;\">\r\n<div class=\"block_thumb_slide_show\" style=\"margin: 0px; padding: 0px; width: 660.015625px; float: left; position: relative; text-align: center; font-size: 0px; line-height: 0;\"><img a=\"\" alt=\"\" bay=\"\" c=\"\" chim=\"\" class=\"btn_icon_show_slide_show\" con=\"\" data-component-caption=\"&lt;p class=\" data-reference-id=\"19086649\" i=\"\" khi=\"\" m=\"\" n=\"\" ng=\"\" nh=\"\" nh.=\"\" o=\"\" src=\"http://l.f32.img.vnecdn.net/2014/05/07/General-Giap-20-Proc-1399431575_660x0.jpg\" style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); outline: none; height: 28px; width: 28px; display: block; background-image: url(http://st.f3.vnecdn.net/responsive/c/v13/images/icons/icon_zoom.png); position: absolute; right: 0px; top: 0px; background-position: 0% 0%; background-repeat: no-repeat no-repeat;\" t=\"\" u=\"\" y=\"\" />&nbsp;</div>\r\n\r\n<div class=\"desc_cation\" style=\"margin: 0px; padding: 10px 13.1875px; width: 633.609375px; float: left; background-color: rgb(245, 245, 245);\">\r\n<p class=\"Normal\" style=\"margin: 0px; padding: 0px;\">&quot;Cuối c&ugrave;ng, c&oacute; tiếng v&ugrave; v&ugrave; tr&ecirc;n bầu trời v&agrave; ch&uacute;ng t&ocirc;i thấy một con chim lớn bay về ph&iacute;a m&igrave;nh. Mọi người chạy tới chỗ trực thăng khi n&oacute; hạ c&aacute;nh v&agrave; Đại tướng xuất hiện, vẫy ch&agrave;o đ&aacute;m đ&ocirc;ng&quot;, b&agrave; kể lại.&nbsp;</p>\r\n</div>\r\n\r\n<div class=\"clear\" style=\"margin: 0px; padding: 0px; clear: both; font-size: 0px; line-height: 0;\">&nbsp;</div>\r\n</div>\r\n\r\n<div class=\"item_slide_show\" style=\"margin: 0px auto 10px; padding: 0px; clear: both; color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: normal;\">\r\n<div class=\"block_thumb_slide_show\" style=\"margin: 0px; padding: 0px; width: 660.015625px; float: left; position: relative; text-align: center; font-size: 0px; line-height: 0;\"><img alt=\"\" class=\"left\" data-component-caption=\"&lt;p&gt;\r\n	Người dân Mường Phăng tụ tập xung quanh trực thăng.&nbsp;&lt;/p&gt;\r\n\" data-reference-id=\"19086650\" src=\"http://l.f32.img.vnecdn.net/2014/05/07/nguyen2012-09-12-Karnow-5-JPG-3326-1381896014-1399433494_660x0.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; line-height: 0; max-width: 100%; float: none; cursor: all-scroll;\" /><a class=\"btn_icon_show_slide_show\" style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); outline: none; height: 28px; width: 28px; display: block; background-image: url(http://st.f3.vnecdn.net/responsive/c/v13/images/icons/icon_zoom.png); position: absolute; right: 0px; top: 0px; background-position: 0% 0%; background-repeat: no-repeat no-repeat;\">&nbsp;</a></div>\r\n\r\n<div class=\"desc_cation\" style=\"margin: 0px; padding: 10px 13.1875px; width: 633.609375px; float: left; background-color: rgb(245, 245, 245);\">\r\n<p style=\"margin: 0px; padding: 0px;\">Người d&acirc;n Mường Phăng tụ tập xung quanh trực 